template.script:
document.addEventListener('DOMContentLoaded', function() {
    const titles = ['Quality Excellence', 'Reliability', 'Collaboration']; 
    const titleElement = document.querySelector('.flex.relative.h-20 .bg-mpa-secondary span');
    const carousel = document.getElementById('default-carousel');

    // Jika carousel tidak ditemukan, keluar dari fungsi
    if (!carousel) return;

    function setTitle(name) { 
        if (titleElement) {
            titleElement.textContent = name; 
        }
    } 

    const items = carousel.querySelectorAll('[data-carousel-item]'); 
    const prevButton = carousel.querySelector('[data-carousel-prev]'); 
    const nextButton = carousel.querySelector('[data-carousel-next]'); 
    const indicators = carousel.querySelectorAll('[data-carousel-slide-to]');
    let currentIndex = 0; 
    let autoplayInterval;

    const paragraphs = [
        "Setiap batu yang kami kirimkan telah melalui proses seleksi dan kontrol mutu yang ketat. Bagi kami, pengalaman adalah bekal dan kualitas adalah komitmen.",
        "Kami berkomitmen untuk selalu dapat diandalkan dalam setiap aspek layanan kami. Ketepatan waktu, konsistensi kualitas, dan profesionalisme adalah prioritas utama.",
        "Kami percaya bahwa kesuksesan terbaik dicapai melalui kerjasama yang erat. Dengan mengedepankan komunikasi terbuka dan saling mendukung, kami membangun hubungan yang berkelanjutan."
    ];

    const paragraphElement = document.getElementById('paragraph-title');

    function showItem(index) { 
        // Update carousel items
        items.forEach(i => {
            i.classList.remove('block');
            i.classList.add('hidden');
        }); 
        items[index].classList.remove('hidden');
        items[index].classList.add('block'); 

        // Update indicators
        indicators.forEach((indicator, i) => {
            if (i === index) {
                indicator.setAttribute('aria-current', 'true');
                indicator.classList.add('bg-mpa-primary');
                indicator.classList.remove('bg-mpa-primary/50');
            } else {
                indicator.setAttribute('aria-current', 'false');
                indicator.classList.remove('bg-mpa-primary');
                indicator.classList.add('bg-mpa-primary/50');
            }
        });

        // Update title and paragraph
        setTitle(titles[index]);
        if (paragraphElement) {
            paragraphElement.textContent = paragraphs[index];
        }
    } 

    // Initialize first item
    showItem(0);

    // Add click event for indicators
    indicators.forEach((indicator, index) => {
        indicator.addEventListener('click', () => {
            currentIndex = index;
            showItem(currentIndex);

            // Reset autoplay timer when manually changing slides
            resetAutoplay();
        });
    });

    prevButton.addEventListener('click', () => { 
        currentIndex = (currentIndex - 1 + items.length) % items.length; 
        showItem(currentIndex); 

        // Reset autoplay timer when manually changing slides
        resetAutoplay();
    }); 

    nextButton.addEventListener('click', () => { 
        currentIndex = (currentIndex + 1) % items.length; 
        showItem(currentIndex); 

        // Reset autoplay timer when manually changing slides
        resetAutoplay();
    }); 

    // Function to start autoplay
    function startAutoplay() {
        autoplayInterval = setInterval(() => {
            currentIndex = (currentIndex + 1) % items.length;
            showItem(currentIndex);
        }, 5000);
    }

    // Function to reset autoplay
    function resetAutoplay() {
        clearInterval(autoplayInterval);
        startAutoplay();
    }

    // Start autoplay
    startAutoplay();

    // Pause autoplay when hovering over carousel
    carousel.addEventListener('mouseenter', () => {
        clearInterval(autoplayInterval);
    });

    // Resume autoplay when mouse leaves carousel
    carousel.addEventListener('mouseleave', () => {
        startAutoplay();
    });
});
